  constructor() {
    super();
    this.state = { data: [{ "name": "Jack" }, { "name": "Jill" }, { "name": "Bill" }] }
  }

  render() {
    return (
      <div>
        <StudentName />
        <ul>
          {this.state.data.map((item) => <ListStudentNames data={item} />)}
        </ul>
      </div>
    );
  }
}
